frappe.web_form.after_load = function() {

    let dropdownContainer = document.createElement("div");
    dropdownContainer.innerHTML = `
        <div>
            <label for="toggle-dropdown">Select Option:</label><br>
            <select id="toggle-dropdown" class="form-control">
                <option value="default" selected>-- Select --</option>
                <option value="Car">Car</option>
                <option value="Allowance">Allowance</option>
            </select>
        </div>
        <hr>
    `;
    document.querySelector(".web-form-wrapper").prepend(dropdownContainer);


    let formFields = document.querySelectorAll(".frappe-control");
    formFields.forEach(field => {
        field.style.display = "none";
    });
    
    let formSection = document.querySelectorAll(".section-head");
    formSection.forEach(field => {
        field.style.display = "none";
    });

    // Step 3: Show/Hide fields based on selection
     document.getElementById("toggle-dropdown").addEventListener("change", function() {
        let selectedValue = this.value;

        if (selectedValue === "Car") {
            formFields.forEach(field => field.style.display = "block");
        } 
        else if (selectedValue === "Allowance") {
            alert("⚠ You selected 'B'. No fields will be shown.");
            formFields.forEach(field => field.style.display = "none");
        } 
        else {
            formFields.forEach(field => field.style.display = "none");
        }
        
        frappe.web_form.set_value("form_type", selectedValue);
        
        
    });
};

