function car_form_fill(user, company, quotation_id, form_name, form_status, form_document) {
    frappe.call({
        method: "alms_app.api.car_process.car_form_fill",
        type: "POST",
        args: { 
            user: user,
            company: company,
            quotation_id: quotation_id,
            form_name: form_name,
            form_status: form_status,
            form_document: form_document,  // Ensure it’s never undefined
        },
        callback: function (response) {
            if (response.message && response.message.status === "success") {
                frappe.msgprint({
                    title: "Success",
                    indicator: "green",
                    message: response.message.message,
                });

                // Reset form or redirect
                frappe.web_form.success && frappe.web_form.success(response.message);
            } else {
                frappe.msgprint({
                    title: "Error",
                    indicator: "red",
                    message: response.message.message || "An unknown error occurred.",
                });
            }
        },
        error: function () {
            frappe.msgprint({
                title: "Error",
                indicator: "red",
                message: "Failed to submit the form. Please try again.",
            });
        },
    });
}

